function J = shadedjet(m)if nargin < 1, m = size(get(gcf,'colormap'),1); endn = max(round(m/4),1);x = (1:n)'/n;y = (n/2:n)'/n;e = ones(length(x),1);r = [0*y; 0*e; x; e; flipud(y)];g = [0*y; x; e; flipud(x); 0*y];b = [y; e; flipud(x); 0*e; 0*y];J = [r g b];while size(J,1) > m   J(1,:) = [];   if size(J,1) > m, J(size(J,1),:) = []; endend%Now modulate the whole thing by a multiplicative factorcolors=16;shades=fix(m/colors);%tmp=(1:shades)/shades;%Specify a -- 0 means the shading goes down all the way to black.a=.4;tmp=a+(1:shades)/shades*(1-a);mfact=zeros(m,1);for c=1:colors	mfact((c-1)*shades + (1:shades))=fliplr(tmp);endmfact=[mfact mfact mfact];J=J.*mfact;